declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    if (!fs.existsSync(this.filePath)) return [];
    const data = fs.readFileSync(this.filePath, 'utf8');
    return JSON.parse(data).tasks || [];
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks[taskIndex];
      tasks.splice(taskIndex, 1);
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    return { id, title: '', done: false, createdAt: '' };
  }
}

export { TaskStore };

declare const require: any;
declare const process: any;
const fs = require('node:fs');
const path = require('node:path');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  node dist/cli.js list --data <path>
  node dist/cli.js add "Task title" --data <path>
  node dist/cli.js done <id> --data <path>
  node dist/cli.js remove <id> --data <path>`);
}

function main() {
  const args = process.argv.slice(2);
  if (args.includes('--help')) {
    help();
    process.exit(0);
  }

  const command = args[0];
  const dataPathIndex = args.indexOf('--data');
  const dataPath = dataPathIndex !== -1 ? args[dataPathIndex + 1] : null;

  if (!command || !dataPath) {
    help();
    process.exit(1);
  }

  const store = new TaskStore(dataPath);

  try {
    switch (command) {
      case 'list':
        console.log(JSON.stringify({ ok: true, tasks: store.list() }));
        break;
      case 'add':
        if (args.length < 2) throw new Error('Title is required');
        const title = args[1];
        console.log(JSON.stringify({ ok: true, task: store.add(title) }));
        break;
      case 'done':
        if (args.length < 2) throw new Error('ID is required');
        const idDone = args[1];
        console.log(JSON.stringify({ ok: true, task: store.done(idDone) }));
        break;
      case 'remove':
        if (args.length < 2) throw new Error('ID is required');
        const idRemove = args[1];
        console.log(JSON.stringify({ ok: true, task: store.remove(idRemove) }));
        break;
      default:
        help();
        process.exit(1);
    }
  } catch (error) {
    console.error(JSON.stringify({ ok: false, error: error.message }));
    process.exit(1);
  }
}

main();
