declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');
import { TaskStore } from './store';

const args = process.argv.slice(2);
const command = args[0];
const filePath = 'tasks.json';
const taskStore = new TaskStore(filePath);

switch (command) {
  case '--help':
    console.log('Usage: node dist/cli.js [command] [options]');
    console.log('Commands:');
    console.log('  add <title>       Add a new task with the given title');
    console.log('  list              List all tasks');
    console.log('  done <id>         Mark a task as done by its ID');
    console.log('  remove <id>       Remove a task by its ID');
    break;

  case 'add':
    if (args.length < 2) {
      console.error('Error: Title is required for adding a new task.');
      process.exit(1);
    }
    const title = args.slice(1).join(' ');
    const task = taskStore.add(title);
    console.log(JSON.stringify(task));
    break;

  case 'list':
    const tasks = taskStore.list();
    console.log(JSON.stringify(tasks));
    break;

  case 'done':
    if (args.length < 2) {
      console.error('Error: Task ID is required to mark a task as done.');
      process.exit(1);
    }
    const taskId = args[1];
    try {
      taskStore.markDone(taskId);
      console.log(`Task with ID ${taskId} marked as done.`);
    } catch (error) {
      console.error(error.message);
    }
    break;

  case 'remove':
    if (args.length < 2) {
      console.error('Error: Task ID is required to remove a task.');
      process.exit(1);
    }
    const taskIdToRemove = args[1];
    try {
      taskStore.remove(taskIdToRemove);
      console.log(`Task with ID ${taskIdToRemove} removed.`);
    } catch (error) {
      console.error(error.message);
    }
    break;

  default:
    console.error('Error: Unknown command. Use --help to see available commands.');
    process.exit(1);
}

declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');

interface Task {
  id: string,
  title: string,
  done: boolean,
  createdAt: string,
  doneAt?: string
}

class TaskStore {
  private tasks: Task[];
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
    this.tasks = [];
    this.load();
  }

  load(): void {
    try {
      const data = fs.readFileSync(this.filePath, 'utf-8');
      this.tasks = JSON.parse(data);
    } catch (error) {
      console.error('Failed to load tasks:', error.message);
    }
  }

  save(): void {
    try {
      fs.writeFileSync(this.filePath, JSON.stringify(this.tasks, null, 2));
    } catch (error) {
      console.error('Failed to save tasks:', error.message);
    }
  }

  add(title: string): Task {
    const task = { id: crypto.randomUUID(), title, done: false, createdAt: new Date().toISOString() };
    this.tasks.push(task);
    this.save();
    return task;
  }

  list(): Task[] {
    return this.tasks;
  }

  markDone(id: string): void {
    const task = this.tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      this.save();
    } else {
      throw new Error(`Task with ID ${id} not found`);
    }
  }

  remove(id: string): void {
    const index = this.tasks.findIndex(t => t.id === id);
    if (index !== -1) {
      this.tasks.splice(index, 1);
      this.save();
    } else {
      throw new Error(`Task with ID ${id} not found`);
    }
  }
}

export { TaskStore };
