      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');
import { TaskStore, Task } from './store';

function help(): void {
  console.log(`
Task Manager CLI

Usage:
- add <title> --data <path>
- list --data <path>
- done <id> --data <path>
- remove <id> --data <path>
`);
}

function parseArgs(): { command: string, args: any } {
  const args = process.argv.slice(2);
  const command = args[0];
  const options: any = {};

  for (let i = 1; i < args.length; i++) {
    if (args[i].startsWith('--')) {
      const [key, value] = args[i].slice(2).split('=');
      options[key] = value || true;
    }
  }

  return { command, args: options };
}

function main(): void {
  const { command, args } = parseArgs();

  if (command === '--help' || !args.data) {
    help();
    return;
  }

  const store = new TaskStore(args.data);
  store.load();

  switch (command) {
    case 'add':
      if (!args.title) {
        console.error('Missing title');
        return;
      }
      const task = store.add(args.title);
      console.log(`Task added: ${task.id} - ${task.title}`);
      break;

    case 'list':
      const tasks = store.list();
      tasks.forEach(task => {
        console.log(`${task.id} - ${task.title} [${task.done ? 'Done' : 'Pending'}]`);
      });
      break;

    case 'done':
      if (!args.id) {
        console.error('Missing id');
        return;
      }
      store.markDone(args.id);
      console.log(`Task ${args.id} marked as done.`);
      break;

    case 'remove':
      if (!args.id) {
        console.error('Missing id');
        return;
      }
      store.remove(args.id);
      console.log(`Task ${args.id} removed.`);
      break;

    default:
      help();
  }
}

main();
