    {
      "path": "src/store.ts",
      "content": "const crypto = require('crypto');

class TaskStore {
  constructor(filePath) {
    this.filePath = filePath;
    this.tasks = [];
  }

  load() {
    try {
      const data = require(this.filePath);
      this.tasks = Array.isArray(data) ? data : [];
    } catch (e) {
      this.tasks = [];
    }
  }

  save() {
    const fs = require('fs');
    fs.writeFileSync(this.filePath, JSON.stringify(this.tasks, null, 2));
  }

  addTask(title) {
    const task = {
      id: crypto.randomBytes(16).toString('hex'),
      title,
      done: false
    };
    this.tasks.push(task);
    this.save();
    return task;
  }

  listTasks() {
    return this.tasks;
  }

  markTaskDone(id) {
    const task = this.tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      this.save();
    }
  }

  removeTask(id) {
    this.tasks = this.tasks.filter(t => t.id !== id);
    this.save();
  }
}

module.exports = TaskStore;"
    },
    {
      "path": "src/cli.ts",
      "content": "const fs = require('fs');
const path = require('path');
const { program } = require('commander');
const TaskStore = require('./store');

program
  .option('--data <path>', 'Path to the data file', './tasks.json')
  .parse(process.argv);

const options = program.opts();
const taskStore = new TaskStore(options.data);
taskStore.load();

program
  .command('add <title>')
  .action((title) => {
    const task = taskStore.addTask(title);
    console.log(`Added task: ${task.title} (ID: ${task.id})`);
  });

program
  .command('list')
  .action(() => {
    const tasks = taskStore.listTasks();
    if (tasks.length === 0) {
      console.log('No tasks found.');
    } else {
      tasks.forEach(task => {
        console.log(`${task.id}: ${task.title} [${task.done ? 'Done' : 'Pending'}]`);
      });
    }
  });

program
  .command('done <id>')
  .action((id) => {
    taskStore.markTaskDone(id);
    console.log(`Marked task ${id} as done.`);
  });

program
  .command('remove <id>')
  .action((id) => {
    taskStore.removeTask(id);
    console.log(`Removed task ${id}.`);
  });

if (!process.argv.slice(2).length) {
  program.outputHelp();
}"
    }
  ],
  "notes": "Added missing files and corrected the require statements in src/store.ts."
