import { readFileSync, writeFileSync } from 'fs';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  private readTasks(): Task[] {
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data);
    } catch (error) {
      return [];
    }
  }

  private writeTasks(tasks: Task[]): void {
    writeFileSync(this.filePath, JSON.stringify(tasks, null, 2));
  }

  addTask(title: string): Task {
    const tasks = this.readTasks();
    const newTask: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    tasks.push(newTask);
    this.writeTasks(tasks);
    return newTask;
  }

  listTasks(): Task[] {
    return this.readTasks();
  }

  markTaskAsDone(id: string): void {
    const tasks = this.readTasks();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      this.writeTasks(tasks);
    }
  }

  removeTask(id: string): void {
    let tasks = this.readTasks();
    tasks = tasks.filter(t => t.id !== id);
    this.writeTasks(tasks);
  }
}

export default TaskStore;

import { argv } from 'process';
import TaskStore from './store';

const args = argv.slice(2);
const command = args[0];
const dataPath = args[args.length - 1];
const taskStore = new TaskStore(dataPath);

if (command === 'add') {
  const title = args.slice(1, -1).join(' ');
  const task = taskStore.addTask(title);
  console.log(`Added task: ${task.title}`);
} else if (command === 'list') {
  const tasks = taskStore.listTasks();
  tasks.forEach(task => {
    console.log(`${task.id}: ${task.title} [${task.done ? 'Done' : 'Pending'}]`);
  });
} else if (command === 'done') {
  const id = args[1];
  taskStore.markTaskAsDone(id);
  console.log(`Marked task with ID ${id} as done.`);
} else if (command === 'remove') {
  const id = args[1];
  taskStore.removeTask(id);
  console.log(`Removed task with ID ${id}.`);
} else if (command === '--help' || command === '-h') {
  console.log('Usage: node dist/cli.js <command> --data <path>');
  console.log('Commands: add, list, done, remove');
} else {
  console.error('Unknown command. Use --help for more information.');
}
