    {
      "path": "src/store.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks[taskIndex];
      tasks.splice(taskIndex, 1);
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    return { id, title: '', done: false, createdAt: '' };
  }
}

export { TaskStore };"
    },
    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>`);
  process.exit(0);
}

const argv = process.argv.slice(2);
const cmd = argv[0];
let dataPath: string | undefined;

for (let i = 1; i < argv.length; i++) {
  if (argv[i] === '--data' && i + 1 < argv.length) {
    dataPath = argv[i + 1];
    break;
  }
}

if (!dataPath) {
  console.error('Missing --data path');
  process.exit(1);
}

const store = new TaskStore(dataPath);

switch (cmd) {
  case 'list':
    const tasks = store.list();
    console.log(JSON.stringify({ ok: true, tasks }));
    break;
  case 'add':
    if (!argv[1]) {
      console.error('Missing title for add command');
      process.exit(1);
    }
    const addedTask = store.add(argv[1]);
    console.log(JSON.stringify({ ok: true, task: addedTask }));
    break;
  case 'done':
    if (!argv[1]) {
      console.error('Missing id for done command');
      process.exit(1);
    }
    const doneTask = store.done(argv[1]);
    console.log(JSON.stringify({ ok: true, task: doneTask }));
    break;
  case 'remove':
    if (!argv[1]) {
      console.error('Missing id for remove command');
      process.exit(1);
    }
    const removedTask = store.remove(argv[1]);
    console.log(JSON.stringify({ ok: true, task: removedTask }));
    break;
  case '--help':
    help();
    break;
  default:
    console.error('Unknown command');
    process.exit(1);
}"
    }
  ],
  "notes": "This project implements a simple task manager CLI using TypeScript and Node.js built-in modules. It includes a `TaskStore` class for managing tasks and a `cli.ts` file for handling command-line interface operations."
}
