declare const require: any;
declare const process: any;

const fs = require('node:fs');
const path = require('node:path');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function showHelp() {
  console.log(`
Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>
  --help
`);
  process.exit(0);
}

function parseArgs() {
  const args = process.argv.slice(2);
  let cmd = args[0];
  let dataPath: string | null = null;
  let titleOrId: string | null = null;

  for (let i = 1; i < args.length; i++) {
    if (args[i] === '--data' && i + 1 < args.length) {
      dataPath = args[i + 1];
      i++;
    } else if (!titleOrId) {
      titleOrId = args[i];
    }
  }

  return { cmd, dataPath, titleOrId };
}

function main() {
  const { cmd, dataPath, titleOrId } = parseArgs();

  if (cmd === '--help') {
    showHelp();
  }

  if (!dataPath) {
    console.error('Missing --data option');
    process.exit(1);
  }

  const store = new TaskStore(dataPath);

  try {
    switch (cmd) {
      case 'list':
        console.log(JSON.stringify({ ok: true, tasks: store.list() }));
        break;
      case 'add':
        if (!titleOrId) throw new Error('Missing title');
        console.log(JSON.stringify({ ok: true, task: store.add(titleOrId) }));
        break;
      case 'done':
        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: store.done(titleOrId) }));
        break;
      case 'remove':
        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: store.remove(titleOrId) }));
        break;
      default:
        console.error(`Unknown command: ${cmd}`);
        process.exit(1);
    }
  } catch (error: any) {
    console.error(JSON.stringify({ ok: false, error: error.message }));
    process.exit(1);
  }
}

main();

import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';
import { v4 as uuidv4 } from 'uuid';

class Task {
  id: string;
  title: string;
  completed: boolean;

  constructor(title: string) {
    this.id = uuidv4();
    this.title = title;
    this.completed = false;
  }
}

class TaskStore {
  private tasks: Task[];
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = join(__dirname, filePath);
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      this.tasks = JSON.parse(data) || [];
    } catch (error) {
      this.tasks = [];
    }
  }

  list(): Task[] {
    return this.tasks;
  }

  add(title: string): Task {
    const task = new Task(title);
    this.tasks.push(task);
    this.save();
    return task;
  }

  done(id: string): Task | null {
    const task = this.tasks.find(t => t.id === id);
    if (task) {
      task.completed = true;
      this.save();
    }
    return task || null;
  }

  remove(id: string): Task | null {
    const index = this.tasks.findIndex(t => t.id === id);
    if (index !== -1) {
      const removedTask = this.tasks.splice(index, 1)[0];
      this.save();
      return removedTask;
    }
    return null;
  }

  private save() {
    writeFileSync(this.filePath, JSON.stringify(this.tasks, null, 2), 'utf-8');
  }
}

export { TaskStore };
