      "path": "src/store.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks.splice(taskIndex, 1)[0];
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    return { id, title: '', done: false, createdAt: '' };
  }
}

export default TaskStore;"
    },
    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');
import TaskStore from './store';

const argv = process.argv.slice(2);
const command = argv[0];
const dataPath = argv.find(arg => arg.startsWith('--data='))?.split('=')[1];

if (command === '--help') {
  console.log(`
Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>
  --help
`);
} else if (command && dataPath) {
  const taskStore = new TaskStore(dataPath);

  switch (command) {
    case 'add':
      const title = argv.find(arg => !arg.startsWith('--')) || '';
      console.log(taskStore.add(title));
      break;
    case 'list':
      console.log(JSON.stringify(taskStore.list(), null, 2));
      break;
    case 'done':
      const id = argv.find(arg => !arg.startsWith('--')) || '';
      console.log(taskStore.done(id));
      break;
    case 'remove':
      const removeId = argv.find(arg => !arg.startsWith('--')) || '';
      console.log(taskStore.remove(removeId));
      break;
    default:
      console.error('Unknown command');
  }
} else {
  console.error('Invalid usage. Use --help for more information.');
}"
