      "path": "src/store.ts",
      "content": "import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';
import { randomUUID } from 'crypto';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = join(__dirname, '..', filePath);
  }

  list(): Task[] {
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        writeFileSync(this.filePath, JSON.stringify({ tasks: [] }, null, 2));
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      tasks[taskIndex].done = true;
      tasks[taskIndex].doneAt = new Date().toISOString();
      writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return tasks[taskIndex];
    }
    throw new Error('Task not found');
  }

  remove(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks.splice(taskIndex, 1)[0];
      writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    throw new Error('Task not found');
  }
}

export { TaskStore };"
    },
    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;

const fs = require('node:fs');
const path = require('node:path');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>`);
  process.exit(0);
}

const args = process.argv.slice(2);
if (args.length === 0 || args[0] === '--help') {
  help();
}

let command = args[0];
let dataPath: string | undefined;
let idOrTitle: string | undefined;

for (let i = 1; i < args.length; i++) {
  if (args[i] === '--data') {
    dataPath = args[i + 1];
    i++;
  } else {
    if (!idOrTitle) {
      idOrTitle = args[i];
    }
  }
}

if (!dataPath) {
  console.error('Missing --data option');
  process.exit(1);
}

const taskStore = new TaskStore(dataPath);

switch (command) {
  case 'add':
    if (!idOrTitle) {
      console.error('Missing title for add command');
      process.exit(1);
    }
    const addedTask = taskStore.add(idOrTitle);
    console.log(JSON.stringify(addedTask, null, 2));
    break;
  case 'list':
    const tasks = taskStore.list();
    console.log(JSON.stringify(tasks, null, 2));
    break;
  case 'done':
    if (!idOrTitle) {
      console.error('Missing id for done command');
      process.exit(1);
    }
    const updatedTask = taskStore.done(idOrTitle);
    console.log(JSON.stringify(updatedTask, null, 2));
    break;
  case 'remove':
    if (!idOrTitle) {
      console.error('Missing id for remove command');
      process.exit(1);
    }
    const removedTask = taskStore.remove(idOrTitle);
    console.log(JSON.stringify(removedTask, null, 2));
    break;
  default:
    help();
}"
    }
