      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;

const fs = require('node:fs');
const path = require('node:path');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function parseArgs() {
  const args = process.argv.slice(2);
  const cmd = args[0];
  let dataPath = '';
  let titleOrId = '';

  for (let i = 1; i < args.length; i++) {
    if (args[i] === '--data') {
      dataPath = args[i + 1];
      i++;
    } else {
      titleOrId = args[i];
    }
  }

  return { cmd, dataPath, titleOrId };
}

function help() {
  console.log(`
Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>
`);
}

async function main() {
  const { cmd, dataPath, titleOrId } = parseArgs();

  if (cmd === '--help' || !dataPath) {
    help();
    return;
  }

  const taskStore = new TaskStore(dataPath);

  try {
    switch (cmd) {
      case 'add':
        if (!titleOrId) throw new Error('Missing title');
        console.log(taskStore.add(titleOrId));
        break;
      case 'list':
        console.log(JSON.stringify(taskStore.list(), null, 2));
        break;
      case 'done':
        if (!titleOrId) throw new Error('Missing id');
        console.log(taskStore.done(titleOrId));
        break;
      case 'remove':
        if (!titleOrId) throw new Error('Missing id');
        console.log(taskStore.remove(titleOrId));
        break;
      default:
        help();
    }
  } catch (error: any) {
    console.error(error.message);
  }
}

main();"
