    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;

const fs = require('node:fs');
const path = require('node:path');
const { TaskStore } = require('./store');

function help() {
  console.log(`Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>`);
  process.exit(0);
}

const argv = process.argv.slice(2);
const cmd = argv[0];
let dataPath: string | undefined;

for (let i = 1; i < argv.length; i++) {
  if (argv[i] === '--data' && i + 1 < argv.length) {
    dataPath = argv[i + 1];
    break;
  }
}

if (!dataPath) {
  help();
}

const store = new TaskStore(dataPath);

switch (cmd) {
  case 'list':
    console.log(JSON.stringify({ ok: true, tasks: store.list() }));
    break;
  case 'add':
    if (!argv[1]) {
      help();
    }
    const title = argv.slice(1).join(' ').replace('--data', '').trim();
    console.log(JSON.stringify({ ok: true, task: store.add(title) }));
    break;
  case 'done':
    if (!argv[1]) {
      help();
    }
    const id = argv[1];
    console.log(JSON.stringify({ ok: true, task: store.done(id) }));
    break;
  case 'remove':
    if (!argv[1]) {
      help();
    }
    const removeId = argv[1];
    console.log(JSON.stringify({ ok: true, task: store.remove(removeId) }));
    break;
  default:
    help();
}
"
