      "path": "src/store.ts",
      "content": "import { randomUUID } from 'node:crypto';
import fs from 'node:fs';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  private readTasks(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  private writeTasks(tasks: Task[]): void {
    const data = JSON.stringify({ tasks }, null, 2);
    fs.writeFileSync(this.filePath, data);
  }

  list(): Task[] {
    return this.readTasks();
  }

  add(title: string): Task {
    const task: Task = {
      id: randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.readTasks();
    tasks.push(task);
    this.writeTasks(tasks);
    return task;
  }

  done(id: string): Task {
    let tasks = this.readTasks();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      this.writeTasks(tasks);
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): Task {
    let tasks = this.readTasks();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks[taskIndex];
      tasks.splice(taskIndex, 1);
      this.writeTasks(tasks);
      return removedTask;
    }
    return { id, title: '', done: false, createdAt: '' };
  }
}

export { TaskStore };"
    },
    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;

import fs from 'node:fs';
import { TaskStore } from './store';

const argv = process.argv.slice(2);
const cmd = argv[0];
const dataPathIndex = argv.indexOf('--data');
const dataPath = dataPathIndex !== -1 ? argv[dataPathIndex + 1] : null;

if (cmd === '--help') {
  console.log(`Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>`);
  process.exit(0);
}

if (!dataPath) {
  console.error('Missing --data option');
  process.exit(1);
}

const store = new TaskStore(dataPath);

try {
  switch (cmd) {
    case 'list':
      const tasks = store.list();
      console.log(JSON.stringify({ ok: true, tasks }));
      break;
    case 'add':
      if (!argv[1]) {
        throw new Error('Missing title');
      }
      const addedTask = store.add(argv[1]);
      console.log(JSON.stringify({ ok: true, task: addedTask }));
      break;
    case 'done':
      if (!argv[1]) {
        throw new Error('Missing id');
      }
      const doneTask = store.done(argv[1]);
      console.log(JSON.stringify({ ok: true, task: doneTask }));
      break;
    case 'remove':
      if (!argv[1]) {
        throw new Error('Missing id');
      }
      const removedTask = store.remove(argv[1]);
      console.log(JSON.stringify({ ok: true, task: removedTask }));
      break;
    default:
      console.error(`Unknown command: ${cmd}`);
      process.exit(1);
  }
} catch (error: any) {
  console.error(JSON.stringify({ ok: false, error: error.message }));
  process.exit(1);
}"
    }
  ],
  "notes": "This project is a simple task manager CLI written in TypeScript. It uses Node.js built-in modules and does not rely on any external npm packages."
