const http = require('http');
const fs = require('fs');
const url = require('url');
const path = require('path');

function createServer(dataPath) {
  const server = http.createServer((req, res) => {
    const parsedUrl = url.parse(req.url, true);
    const method = req.method.toUpperCase();
    let body = '';

    req.on('data', chunk => {
      body += chunk.toString();
    });

    req.on('end', () => {
      if (parsedUrl.pathname === '/health' && method === 'GET') {
        res.writeHead(200, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ ok: true }));
      } else if (parsedUrl.pathname === '/todos' && method === 'GET') {
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false }));
          } else {
            const todos = JSON.parse(data);
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: true, todos }));
          }
        });
      } else if (parsedUrl.pathname === '/todos' && method === 'POST') {
        const todo = JSON.parse(body);
        fs.readFile(dataPath, 'utf8', (err, data) => {
          let todos = [];
          if (!err) {
            todos = JSON.parse(data);
          }
          todo.id = Date.now().toString();
          todo.createdAt = new Date().toISOString();
          todos.push(todo);
          fs.writeFile(dataPath, JSON.stringify(todos), 'utf8', err => {
            if (err) {
              res.writeHead(500, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: false }));
            } else {
              res.writeHead(201, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: true, todo }));
            }
          });
        });
      } else if (parsedUrl.pathname.startsWith('/todos/') && method === 'GET') {
        const id = parsedUrl.pathname.split('/')[2];
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false }));
          } else {
            const todos = JSON.parse(data);
            const todo = todos.find(t => t.id === id);
            if (todo) {
              res.writeHead(200, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: true, todo }));
            } else {
              res.writeHead(404, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
            }
          }
        });
      } else if (parsedUrl.pathname.startsWith('/todos/') && method === 'PATCH') {
        const id = parsedUrl.pathname.split('/')[2];
        fs.readFile(dataPath, 'utf8', (err, data) => {
          let todos = [];
          if (!err) {
            todos = JSON.parse(data);
          }
          const todoIndex = todos.findIndex(t => t.id === id);
          if (todoIndex !== -1) {
            const update = JSON.parse(body);
            const updatedTodo = { ...todos[todoIndex], ...update };
            if (update.done && !updatedTodo.doneAt) {
              updatedTodo.doneAt = new Date().toISOString();
            }
            todos[todoIndex] = updatedTodo;
            fs.writeFile(dataPath, JSON.stringify(todos), 'utf8', err => {
              if (err) {
                res.writeHead(500, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ ok: false }));
              } else {
                res.writeHead(200, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ ok: true, todo: updatedTodo }));
              }
            });
          } else {
            res.writeHead(404, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
          }
        });
      } else if (parsedUrl.pathname.startsWith('/todos/') && method === 'DELETE') {
        const id = parsedUrl.pathname.split('/')[2];
        fs.readFile(dataPath, 'utf8', (err, data) => {
          let todos = [];
          if (!err) {
            todos = JSON.parse(data);
          }
          const todoIndex = todos.findIndex(t => t.id === id);
          if (todoIndex !== -1) {
            todos.splice(todoIndex, 1);
            fs.writeFile(dataPath, JSON.stringify(todos), 'utf8', err => {
              if (err) {
                res.writeHead(500, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ ok: false }));
              } else {
                res.writeHead(200, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ ok: true }));
              }
            });
          } else {
            res.writeHead(404, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
          }
        });
      } else if (parsedUrl.pathname === '/openapi.json' && method === 'GET') {
        fs.readFile(path.join(__dirname, '../openapi.json'), 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false }));
          } else {
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(data);
          }
        });
      } else {
        res.writeHead(404, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
      }
    });
  });

  return server;
}

module.exports = createServer;"
