// Your existing server code here
const http = require('http');
const fs = require('fs');
const path = require('path');

function createServer(dataPath) {
  const server = http.createServer((req, res) => {
    let body = '';
    req.on('data', chunk => {
      body += chunk.toString();
    });

    req.on('end', () => {
      if (req.method === 'GET' && req.url === '/health') {
        res.writeHead(200, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ status: 'healthy' }));
      } else if (req.method === 'GET' && req.url === '/todos') {
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: 'Internal Server Error' }));
          } else {
            const todos = JSON.parse(data);
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify(todos));
          }
        });
      } else if (req.method === 'POST' && req.url === '/todos') {
        const newTodo = JSON.parse(body);
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: 'Internal Server Error' }));
          } else {
            const todos = JSON.parse(data);
            newTodo.id = Date.now().toString();
            newTodo.done = false;
            todos.todos.push(newTodo);
            fs.writeFile(dataPath, JSON.stringify(todos), (err) => {
              if (err) {
                res.writeHead(500, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ error: 'Internal Server Error' }));
              } else {
                res.writeHead(201, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify(newTodo));
              }
            });
          }
        });
      } else if (req.method === 'GET' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/')[2];
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: 'Internal Server Error' }));
          } else {
            const todos = JSON.parse(data);
            const todo = todos.todos.find(t => t.id === id);
            if (todo) {
              res.writeHead(200, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify(todo));
            } else {
              res.writeHead(404, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ error: 'Not Found' }));
            }
          }
        });
      } else if (req.method === 'PATCH' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/')[2];
        const update = JSON.parse(body);
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: 'Internal Server Error' }));
          } else {
            const todos = JSON.parse(data);
            const todoIndex = todos.todos.findIndex(t => t.id === id);
            if (todoIndex !== -1) {
              todos.todos[todoIndex].done = update.done;
              fs.writeFile(dataPath, JSON.stringify(todos), (err) => {
                if (err) {
                  res.writeHead(500, { 'Content-Type': 'application/json' });
                  res.end(JSON.stringify({ error: 'Internal Server Error' }));
                } else {
                  res.writeHead(200, { 'Content-Type': 'application/json' });
                  res.end(JSON.stringify(todos.todos[todoIndex]));
                }
              });
            } else {
              res.writeHead(404, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ error: 'Not Found' }));
            }
          }
        });
      } else if (req.method === 'DELETE' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/')[2];
        fs.readFile(dataPath, 'utf8', (err, data) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: 'Internal Server Error' }));
          } else {
            const todos = JSON.parse(data);
            const todoIndex = todos.todos.findIndex(t => t.id === id);
            if (todoIndex !== -1) {
              todos.todos.splice(todoIndex, 1);
              fs.writeFile(dataPath, JSON.stringify(todos), (err) => {
                if (err) {
                  res.writeHead(500, { 'Content-Type': 'application/json' });
                  res.end(JSON.stringify({ error: 'Internal Server Error' }));
                } else {
                  res.writeHead(204);
                  res.end();
                }
              });
            } else {
              res.writeHead(404, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ error: 'Not Found' }));
            }
          }
        });
      } else {
        res.writeHead(404, { 'Content-Type': 'text/plain' });
        res.end('Not Found');
      }
    });
  });

  return server;
}

module.exports = createServer;
