const http = require('http');
const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

// Function to generate a unique ID
function generateUniqueId() {
  return crypto.randomBytes(16).toString('hex');
}

// Function to read the todos file
function readTodosFile(filePath) {
  try {
    const data = fs.readFileSync(filePath, 'utf8');
    return JSON.parse(data);
  } catch (error) {
    if (error.code === 'ENOENT') {
      return [];
    }
    throw error;
  }
}

// Function to write the todos file
function writeTodosFile(filePath, data) {
  fs.writeFileSync(filePath, JSON.stringify(data, null, 2), 'utf8');
}

// Create server
const server = http.createServer((req, res) => {
  const filePath = path.join(__dirname, 'todos.json');
  let todos = readTodosFile(filePath);

  // Handle GET /health
  if (req.method === 'GET' && req.url === '/health') {
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(JSON.stringify({ ok: true }));
    return;
  }

  // Handle GET /todos
  if (req.method === 'GET' && req.url === '/todos') {
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(JSON.stringify({ ok: true, todos }));
    return;
  }

  // Handle POST /todos
  if (req.method === 'POST' && req.url === '/todos') {
    let body = '';
    req.on('data', chunk => {
      body += chunk.toString();
    });
    req.on('end', () => {
      const newTodo = JSON.parse(body);
      newTodo.id = generateUniqueId();
      newTodo.createdAt = new Date().toISOString();
      todos.push(newTodo);
      writeTodosFile(filePath, todos);
      res.writeHead(201, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ ok: true, todo: newTodo }));
    });
    return;
  }

  // Handle GET /todos/:id
  const id = req.url.match(/^\/todos\/(.*)$/);
  if (req.method === 'GET' && id) {
    const todo = todos.find(t => t.id === id[1]);
    if (todo) {
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ ok: true, todo }));
    } else {
      res.writeHead(404, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
    }
    return;
  }

  // Handle PATCH /todos/:id
  if (req.method === 'PATCH' && id) {
    let body = '';
    req.on('data', chunk => {
      body += chunk.toString();
    });
    req.on('end', () => {
      const update = JSON.parse(body);
      const todoIndex = todos.findIndex(t => t.id === id[1]);
      if (todoIndex !== -1) {
        todos[todoIndex] = { ...todos[todoIndex], ...update };
        if (update.done && !todos[todoIndex].doneAt) {
          todos[todoIndex].doneAt = new Date().toISOString();
        }
        writeTodosFile(filePath, todos);
        res.writeHead(200, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ ok: true, todo: todos[todoIndex] }));
      } else {
        res.writeHead(404, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
      }
    });
    return;
  }

  // Handle DELETE /todos/:id
  if (req.method === 'DELETE' && id) {
    const todoIndex = todos.findIndex(t => t.id === id[1]);
    if (todoIndex !== -1) {
      todos.splice(todoIndex, 1);
      writeTodosFile(filePath, todos);
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ ok: true }));
    } else {
      res.writeHead(404, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
    }
    return;
  }

  // Handle all other requests
  res.writeHead(404, { 'Content-Type': 'text/plain' });
  res.end('Not Found');
});

// Start server
const PORT = process.env.PORT || 3000;
server.listen(PORT, () => {
  console.log(`Server is running on port ${PORT}`);
});"
