{
  "name": "task-manager",
  "version": "1.0.0",
  "description": "A simple task manager CLI built using TypeScript.",
  "main": "dist/cli.js",
  "scripts": {
    "start": "tsc && node dist/cli.js"
  },
  "type": "commonjs"
}

import { randomUUID as uuidv4 } from 'node:crypto';
import fs from 'node:fs';

export class Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;

  constructor(title: string) {
    this.id = uuidv4();
    this.title = title;
    this.done = false;
    this.createdAt = new Date().toISOString();
  }
}

export class TaskStore {
  filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    if (!fs.existsSync(this.filePath)) return [];
    const data = fs.readFileSync(this.filePath, 'utf8');
    return JSON.parse(data).tasks || [];
  }

  add(title: string): Task {
    const task = new Task(title);
    const tasks = this.list();
    tasks.push(task);
    fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    const tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      tasks[taskIndex].done = true;
      tasks[taskIndex].doneAt = new Date().toISOString();
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return tasks[taskIndex];
  }

  remove(id: string): Task {
    const tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks[taskIndex];
      tasks.splice(taskIndex, 1);
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    throw new Error('Task not found');
  }
}

const { TaskStore } = require('./store');

function usage(): string {
  return 'Usage:\n  list --data <path>\n  add <title> --data <path>\n  done <id> --data <path>\n  remove <id> --data <path>\n  --help';
}

function parseDataPath(args: string[]): string | null {
  const idx = args.indexOf('--data');
  if (idx >= 0 && idx + 1 < args.length) return args[idx + 1];
  return null;
}

function firstPositional(args: string[]): string | null {
  for (const token of args) {
    if (!token.startsWith('--')) return token;
  }
  return null;
}

function main(): number {
  const argv = process.argv.slice(2);
  const cmd = String(argv[0] || '');

  if (cmd === '' || cmd === '--help') {
    console.log(usage());
    return 0;
  }

  const dataPath = parseDataPath(argv);
  if (!dataPath) {
    console.error('Missing --data <path>');
    return 1;
  }

  const value = firstPositional(argv.slice(1));
  const store = new TaskStore(dataPath);

  try {
    if (cmd === 'list') {
      console.log(JSON.stringify({ ok: true, tasks: store.list() }));
      return 0;
    }
    if (cmd === 'add') {
      if (!value) throw new Error('Missing title');
      console.log(JSON.stringify({ ok: true, task: store.add(value) }));
      return 0;
    }
    if (cmd === 'done') {
      if (!value) throw new Error('Missing id');
      console.log(JSON.stringify({ ok: true, task: store.done(value) }));
      return 0;
    }
    if (cmd === 'remove') {
      if (!value) throw new Error('Missing id');
      console.log(JSON.stringify({ ok: true, task: store.remove(value) }));
      return 0;
    }
    console.error('Unknown command');
    return 1;
  } catch (error: any) {
    console.error(JSON.stringify({ ok: false, error: String(error?.message || error) }));
    return 1;
  }
}

const exitCode = main();
if (typeof process?.exit === "function") process.exit(exitCode);
