import { readFileSync, writeFileSync } from 'node:fs';
import { randomUUID } from 'node:crypto';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;
  private tasks: Task[] = [];

  constructor(filePath: string) {
    this.filePath = filePath;
    if (fs.existsSync(this.filePath)) {
      const data = readFileSync(this.filePath, 'utf8');
      this.tasks = JSON.parse(data);
    }
  }

  list(): Task[] {
    return this.tasks;
  }

  add(title: string): Task {
    const task: Task = {
      id: randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    this.tasks.push(task);
    this.save();
    return task;
  }

  done(id: string): Task | null {
    const task = this.tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      this.save();
    }
    return task || null;
  }

  remove(id: string): Task | null {
    const index = this.tasks.findIndex(t => t.id === id);
    if (index !== -1) {
      const removedTask = this.tasks.splice(index, 1)[0];
      this.save();
      return removedTask;
    }
    return null;
  }

  private save() {
    writeFileSync(this.filePath, JSON.stringify({ tasks: this.tasks }, null, 2), 'utf8');
  }
}

export { TaskStore };

declare const require: any;
declare const process: any;
const fs = require('node:fs');
import { TaskStore } from './store';

function parseArgs() {
  const args = process.argv.slice(2);
  const cmd = args[0];
  let dataPath = 'tasks.json';
  for (let i = 1; i < args.length; i++) {
    if (args[i] === '--data' && i + 1 < args.length) {
      dataPath = args[i + 1];
      break;
    }
  }
  return { cmd, dataPath };
}

function help() {
  console.log(`Usage:
  node dist/cli.js add <title> --data <path>
  node dist/cli.js list --data <path>
  node dist/cli.js done <id> --data <path>
  node dist/cli.js remove <id> --data <path>`);
  process.exit(0);
}

async function main() {
  const { cmd, dataPath } = parseArgs();

  if (cmd === '--help') {
    help();
  }

  const store = new TaskStore(dataPath);

  try {
    switch (cmd) {
      case 'list':
        console.log(JSON.stringify({ ok: true, tasks: store.list() }));
        break;
      case 'add':
        if (!process.argv[3]) throw new Error('Title is required for add command');
        const title = process.argv[3];
        console.log(JSON.stringify({ ok: true, task: store.add(title) }));
        break;
      case 'done':
        if (!process.argv[3]) throw new Error('ID is required for done command');
        const doneTask = store.done(process.argv[3]);
        console.log(JSON.stringify({ ok: true, task: doneTask || {} }));
        break;
      case 'remove':
        if (!process.argv[3]) throw new Error('ID is required for remove command');
        const removedTask = store.remove(process.argv[3]);
        console.log(JSON.stringify({ ok: true, task: removedTask || {} }));
        break;
      default:
        help();
    }
  } catch (error) {
    console.error(JSON.stringify({ ok: false, error: error.message }));
    process.exit(1);
  }
}

main();
