      "path": "src/server.js",
      "content": "const { createServer } = require('http');
const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

function createServer({ dataPath }) {
  let todos = [];
  try {
    const data = fs.readFileSync(dataPath);
    todos = JSON.parse(data);
  } catch (err) {
    // If the file doesn't exist, we'll create it later (empty array)
  }

  const server = createServer((req, res) => {
    let body = '';
    req.on('data', chunk => {
      body += chunk.toString();
    });
    req.on('end', () => {
      // Parse the body
      if (req.method === 'GET' && req.url === '/health') {
        sendResponse(res, 200, { ok: true });
      } else if (req.method === 'GET' && req.url === '/openapi.json') {
        const openapiSpec = JSON.parse(fs.readFileSync(path.join(__dirname, '..', 'openapi.json')));
        sendResponse(res, 200, openapiSpec);
      } else if (req.method === 'GET' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/').pop();
        const todo = todos.find(todo => todo.id === id);
        if (todo) sendResponse(res, 200, { ok: true, todos: [todo] }); else sendResponse(res, 404, { message: 'Not Found' });
      } else if (req.method === 'GET' && req.url === '/todos') {
        sendResponse(res, 200, { ok: true, todos });
      } else if (req.method === 'POST' && req.url === '/todos') {
        const parsedBody = JSON.parse(body);
        if (!parsedBody.title) return sendResponse(res, 400, { message: 'Bad Request', errors: ['title is required'] });
        const id = crypto.randomUUID();
        todos.push({ id, title: parsedBody.title, done: false });
        fs.writeFileSync(dataPath, JSON.stringify(todos));
        sendResponse(res, 201, { ok: true, todo: { id, title: parsedBody.title } });
      } else if (req.method === 'PATCH' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/').pop();
        const todoIndex = todos.findIndex(todo => todo.id === id);
        if (todoIndex === -1) return sendResponse(res, 404, { message: 'Not Found' });
        const parsedBody = JSON.parse(body);
        if (!parsedBody.done) return sendResponse(res, 400, { message: 'Bad Request', errors: ['done is required'] });
        todos[todoIndex].done = parsedBody.done;
        fs.writeFileSync(dataPath, JSON.stringify(todos));
        sendResponse(res, 200, { ok: true, todo: todos[todoIndex] });
      } else if (req.method === 'DELETE' && req.url.startsWith('/todos/')) {
        const id = req.url.split('/').pop();
        todos = todos.filter(todo => todo.id !== id);
        fs.writeFileSync(dataPath, JSON.stringify(todos));
        sendResponse(res, 204);
      } else {
        sendResponse(res, 405, { message: 'Method Not Allowed' });
      }
    });
  });

  return server;
}

function sendResponse(res, status, data = {}) {
  res.statusCode = status;
  res.setHeader('Content-Type', 'application/json');
  res.end(JSON.stringify(data));
}

module.exports = { createServer };
