import random

class MarkovChain:
    def __init__(self):
        self.chain = {}

    def add_sentence(self, sentence):
        for i in range(len(sentence) - 1):
            if sentence[i] not in self.chain:
                self.chain[sentence[i]] = []
            self.chain[sentence[i]].append(sentence[i + 1])

    def generate_text(self, length):
        seed = random.choice(list(self.chain.keys()))
        text = seed
        while len(text) < length:
            if seed in self.chain:
                next_char = random.choice(self.chain[seed])
                text += next_char
                seed = next_char
            else:
                break
        return text


def train_model(input_text):
    model = MarkovChain()
    sentences = input_text.split('\n') if '\n' in input_text else input_text.split('. ')
    for sentence in sentences:
        model.add_sentence(sentence + '.')
    return model


def generate_text(model, length):
    return model.generate_text(length)

import unittest
from mini_ai.markov import MarkovChain, train_model, generate_text

class TestMarkovChain(unittest.TestCase):
    def test_add_sentence(self):
        model = MarkovChain()
        model.add_sentence('hello')
        self.assertEqual(model.chain, {'h': ['e'], 'e': ['l'], 'l': ['l', 'o'], 'o': [None]})

    def test_generate_text(self):
        model = train_model('he')
        text = generate_text(model, 5)
        self.assertTrue(len(text) == 5)

if __name__ == '__main__':
    unittest.main()
