import random
import json

class MarkovChain:
    def __init__(self):
        self.chain = {}

    def add_text(self, text):
        words = text.split()
        for i in range(len(words) - 1):
            if words[i] not in self.chain:
                self.chain[words[i]] = []
            self.chain[words[i]].append(words[i + 1])

    def generate_text(self, length):
        first_word = random.choice(list(self.chain.keys()))
        text = [first_word]
        while len(text) < length:
            next_word = random.choice(self.chain[text[-1]])
            text.append(next_word)
        return ' '.join(text)

def train(model, text):
    model.add_text(text)

def generate(model_file, length):
    with open(model_file, 'r') as f:
        chain = json.load(f)
    model = MarkovChain()
    for key, value in chain.items():
        model.chain[key] = value
    return model.generate_text(length)
