declare const require: any;
declare const process: any;
const fs = require('node:fs');
const path = require('node:path');

export interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;
  private tasks: Task[] = [];

  constructor(filePath: string) {
    this.filePath = filePath;
    if (fs.existsSync(filePath)) {
      try {
        const data = fs.readFileSync(filePath, 'utf8');
        this.tasks = JSON.parse(data) || [];
      } catch (e) {
        console.error('Failed to read task file:', e);
      }
    }
  }

  list(): Task[] {
    return [...this.tasks];
  }

  add(title: string): Task {
    const newTask = {
      id: Date.now().toString(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    this.tasks.push(newTask);
    this.save();
    return newTask;
  }

  done(id: string): Task | undefined {
    const task = this.tasks.find(t => t.id === id);
    if (task && !task.done) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      this.save();
    }
    return task;
  }

  remove(id: string): Task | undefined {
    const index = this.tasks.findIndex(t => t.id === id);
    if (index !== -1) {
      const removedTask = this.tasks.splice(index, 1)[0];
      this.save();
      return removedTask;
    }
    return undefined;
  }

  private save() {
    try {
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks: this.tasks }, null, 2));
    } catch (e) {
      console.error('Failed to write task file:', e);
    }
  }
}

export { TaskStore };

declare const require: any;
declare const process: any;
const fs = require('node:fs');
const path = require('node:path');
import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  node dist/cli.js --help
  node dist/cli.js list --data <path>
  node dist/cli.js add <title> --data <path>
  node dist/cli.js done <id> --data <path>
  node dist/cli.js remove <id> --data <path>`);
}

function main() {
  if (process.argv[2] === '--help') {
    help();
    process.exit(0);
  }

  const dataPath = process.argv.find(arg => arg.startsWith('--data='))?.split('=')[1];
  if (!dataPath) {
    console.error('Missing --data option');
    process.exit(1);
  }

  const store = new TaskStore(dataPath);

  switch (process.argv[2]) {
    case 'list':
      console.log(JSON.stringify({ ok: true, tasks: store.list() }));
      break;
    case 'add':
      const title = process.argv.find(arg => !arg.startsWith('--')) || '';
      if (!title) {
        console.error('Missing task title');
        process.exit(1);
      }
      console.log(JSON.stringify({ ok: true, task: store.add(title) }));
      break;
    case 'done':
      const idDone = process.argv.find(arg => !arg.startsWith('--')) || '';
      if (!idDone) {
        console.error('Missing task ID');
        process.exit(1);
      }
      console.log(JSON.stringify({ ok: true, task: store.done(idDone) }));
      break;
    case 'remove':
      const idRemove = process.argv.find(arg => !arg.startsWith('--')) || '';
      if (!idRemove) {
        console.error('Missing task ID');
        process.exit(1);
      }
      console.log(JSON.stringify({ ok: true, task: store.remove(idRemove) }));
      break;
    default:
      help();
      process.exit(1);
  }
}

main();
