{
  import { Task } from './task';

  export class TaskStore {
    constructor(private dataPath: string) {}
    list(): Task[] {
      const tasks = JSON.parse(fs.readFileSync(this.dataPath, 'utf8')).map(JSON.parse);
      return tasks;
    }
    add(title: string): Task {
      const task = { title, done: false, createdAt: new Date().toISOString() } as any;
      const updatedTasks = [...this.list(), task];
      fs.writeFileSync(this.dataPath, JSON.stringify(updatedTasks));
      return task;
    }
    done(id: string): Task {
      const tasks = this.list();
      for (let i = 0; i < tasks.length; i++) {
        if (tasks[i].id === id) {
          tasks[i].done = true;
          break;
        }
      }
      fs.writeFileSync(this.dataPath, JSON.stringify(tasks));
      return tasks.find(t => t.id === id)! as Task;
    }
    remove(id: string): Task {
      const tasks = this.list();
      const updatedTasks = tasks.filter(task => task.id !== id);
      fs.writeFileSync(this.dataPath, JSON.stringify(updatedTasks));
      return tasks.find(t => t.id === id)! as Task;
    }
  }
}

