const http = require('http');
const fs = require('fs');
const url = require('url');

function createServer({ dataPath }) {
  let todos = [];
  try {
    const rawData = fs.readFileSync(dataPath, 'utf8');
    if (rawData) {
      const parsedData = JSON.parse(rawData);
      if (Array.isArray(parsedData.todos)) {
        todos = parsedData.todos;
      }
    }
  } catch (err) {
    console.warn('Error reading or parsing data file:', err);
  }

  function saveTodos() {
    fs.writeFileSync(dataPath, JSON.stringify({ todos }), 'utf8');
  }

  function parseBody(req) {
    return new Promise((resolve) => {
      let body = '';
      req.on('data', chunk => (body += chunk));
      req.on('end', () => {
        try {
          resolve(JSON.parse(body));
        } catch (e) {
          resolve(null);
        }
      });
    });
  }

  return http.createServer(async (req, res) => {
    const parsedUrl = url.URL(req.url, `http://${req.headers.host}/`);
    const pathName = parsedUrl.pathname;
    const query = parsedUrl.searchParams;
    let id;

    if (pathName.includes('/todos/')) {
      [, id] = pathName.split('/todos/');
    }

    res.setHeader('Content-Type', 'application/json');

    switch (`${req.method}-${pathName}`) {
      case 'GET-/health':
        res.writeHead(200);
        res.end(JSON.stringify({ ok: true }));
        break;
      case 'GET-/openapi.json':
        fs.readFile('openapi.json', (err, data) => {
          if (err) {
            res.writeHead(500);
            res.end(JSON.stringify({ error: 'OpenAPI file not found' }));
          } else {
            res.setHeader('Content-Type', 'application/json');
            res.writeHead(200);
            res.end(data);
          }
        });
        break;
      case 'GET-/todos':
        res.writeHead(200);
        res.end(JSON.stringify({ ok: true, todos }));
        break;
      case `GET-/todos/${id}`:
        const todo = todos.find(t => t.id === id);
        if (todo) {
          res.writeHead(200);
          res.end(JSON.stringify({ ok: true, todo }));
        } else {
          res.writeHead(404);
          res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
        }
        break;
      case 'POST-/todos':
        const newTodo = await parseBody(req);
        if (!newTodo || typeof newTodo.title !== 'string') {
          res.writeHead(400);
          res.end(JSON.stringify({ ok: false, error: 'Invalid todo format' }));
        } else {
          const createdTodo = { id: Date.now().toString(), title: newTodo.title, done: false, createdAt: new Date().toISOString() };
          todos.push(createdTodo);
          saveTodos();
          res.writeHead(201);
          res.end(JSON.stringify({ ok: true, todo: createdTodo }));
        }
        break;
      case `PATCH-/todos/${id}`:
        const updatedData = await parseBody(req);
        const index = todos.findIndex(t => t.id === id);
        if (index >= 0) {
          if (updatedData.done !== undefined && typeof updatedData.done === 'boolean') {
            todos[index].done = updatedData.done;
          }
          saveTodos();
          res.writeHead(200);
          res.end(JSON.stringify({ ok: true, todo: todos[index] }));
        } else {
          res.writeHead(404);
          res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
        }
        break;
      case `DELETE-/todos/${id}`:
        const deleteIndex = todos.findIndex(t => t.id === id);
        if (deleteIndex >= 0) {
          todos.splice(deleteIndex, 1);
          saveTodos();
          res.writeHead(200);
          res.end(JSON.stringify({ ok: true }));
        } else {
          res.writeHead(404);
          res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
        }
        break;
      default:
        res.writeHead(404);
        res.end(JSON.stringify({ ok: false, error: 'Not Found' }));
    }
  });
}

module.exports = { createServer };

{
  "openapi": "3.0.0",
  "info": {
    "title": "Todo API",
    "version": "1.0.0"
  },
  "paths": {
    "/health": {
      "get": {
        "summary": "Check if the server is running",
        "responses": {
          "200": {
            "description": "Server is up and running",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "ok": {
                      "type": "boolean",
                      "example": true
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/todos": {
      "get": {
        "summary": "Get all todos",
        "responses": {
          "200": {
            "description": "List of todos",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "ok": {
                      "type": "boolean",
                      "example": true
                    },
                    "todos": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/Todo"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "summary": "Create a new todo",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/NewTodo"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Created todo",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Todo"
                }
              }
            }
          },
          "400": {
            "description": "Invalid input data",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/todos/{id}": {
      "parameters": [
        {
          "name": "id",
          "in": "path",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ],
      "get": {
        "summary": "Get a todo by ID",
        "responses": {
          "200": {
            "description": "A todo",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Todo"
                }
              }
            }
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "patch": {
        "summary": "Update a todo",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/UpdatedTodo"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Updated todo",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Todo"
                }
              }
            }
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "delete": {
        "summary": "Delete a todo",
        "responses": {
          "200": {
            "description": "Todo deleted",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Success"
                }
              }
            }
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Todo": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "title": {
            "type": "string"
          },
          "done": {
            "type": "boolean"
          },
          "createdAt": {
            "type": "string",
            "format": "date-time"
          }
        }
      },
      "NewTodo": {
        "type": "object",
        "required": [
          "title"
        ],
        "properties": {
          "title": {
            "type": "string"
          }
        }
      },
      "UpdatedTodo": {
        "type": "object",
        "properties": {
          "done": {
            "type": "boolean"
          }
        }
      },
      "Error": {
        "type": "object",
        "properties": {
          "ok": {
            "type": "boolean",
            "example": false
          },
          "error": {
            "type": "string"
          }
        }
      },
      "Success": {
        "type": "object",
        "properties": {
          "ok": {
            "type": "boolean",
            "example": true
          }
        }
      }
    }
  }
}
