declare const require: any;
declare const process: any;
const fs = require("node:fs");
const crypto = require("node:crypto");

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      tasks[taskIndex].done = true;
      tasks[taskIndex].doneAt = new Date().toISOString();
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return tasks[taskIndex];
    }
    throw new Error(`Task with id ${id} not found`);
  }

  remove(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const removedTask = tasks.splice(taskIndex, 1)[0];
      fs.writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
      return removedTask;
    }
    throw new Error(`Task with id ${id} not found`);
  }
}

export { TaskStore };

declare const require: any;
declare const process: any;
const fs = require("node:fs");
const crypto = require("node:crypto");
import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  node dist/cli.js add <title> --data <path>
  node dist/cli.js list --data <path>
  node dist/cli.js done <id> --data <path>
  node dist/cli.js remove <id> --data <path>`);
  process.exit(0);
}

const argv = process.argv.slice(2);
const cmd = argv[0];
let dataPath: string | undefined;

for (let i = 1; i < argv.length; i++) {
  if (argv[i] === '--data' && i + 1 < argv.length) {
    dataPath = argv[i + 1];
    break;
  }
}

if (!dataPath) {
  console.error('Missing --data option');
  process.exit(1);
}

const store = new TaskStore(dataPath);

switch (cmd) {
  case 'add':
    if (argv.length < 2) {
      console.error('Missing title for add command');
      process.exit(1);
    }
    const title = argv[1];
    try {
      const task = store.add(title);
      console.log(JSON.stringify({ ok: true, task }));
    } catch (error: any) {
      console.error(JSON.stringify({ ok: false, error: error.message }));
      process.exit(1);
    }
    break;
  case 'list':
    try {
      const tasks = store.list();
      console.log(JSON.stringify({ ok: true, tasks }));
    } catch (error: any) {
      console.error(JSON.stringify({ ok: false, error: error.message }));
      process.exit(1);
    }
    break;
  case 'done':
    if (argv.length < 2) {
      console.error('Missing id for done command');
      process.exit(1);
    }
    const id = argv[1];
    try {
      const task = store.done(id);
      console.log(JSON.stringify({ ok: true, task }));
    } catch (error: any) {
      console.error(JSON.stringify({ ok: false, error: error.message }));
      process.exit(1);
    }
    break;
  case 'remove':
    if (argv.length < 2) {
      console.error('Missing id for remove command');
      process.exit(1);
    }
    const removeId = argv[1];
    try {
      const task = store.remove(removeId);
      console.log(JSON.stringify({ ok: true, task }));
    } catch (error: any) {
      console.error(JSON.stringify({ ok: false, error: error.message }));
      process.exit(1);
    }
    break;
  case '--help':
    help();
    break;
  default:
    console.error('Unknown command');
    process.exit(1);
}
