declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');
import TaskStore from './store';

function showHelp() {
  console.log(`
Task Manager CLI

Usage:
- add <title> --data <path>
- list --data <path>
- done <id> --data <path>
- remove <id> --data <path>
`);
}

if (process.argv.length < 3) {
  showHelp();
  process.exit(1);
}

const command = process.argv[2];
let filePath: string | undefined;
let title: string | undefined;
let id: string | undefined;

for (let i = 3; i < process.argv.length; i++) {
  if (process.argv[i] === '--data') {
    if (i + 1 < process.argv.length) {
      filePath = process.argv[i + 1];
      i++;
    } else {
      console.error('Missing file path after --data');
      process.exit(1);
    }
  } else if (!title && !id) {
    if (command === 'add') {
      title = process.argv[i];
    } else {
      id = process.argv[i];
    }
  }
}

if (!filePath) {
  console.error('Missing --data option');
  process.exit(1);
}

const taskStore = new TaskStore(filePath);

switch (command) {
  case 'add':
    if (!title) {
      console.error('Missing title for add command');
      process.exit(1);
    }
    const addedTask = taskStore.add(title);
    console.log(JSON.stringify({ Added: addedTask }, null, 2));
    break;
  case 'list':
    const tasks = taskStore.list();
    if (tasks.length === 0) {
      console.log('No tasks found.');
    } else {
      console.log(JSON.stringify({ Tasks: tasks }, null, 2));
    }
    break;
  case 'done':
    if (!id) {
      console.error('Missing ID for done command');
      process.exit(1);
    }
    taskStore.done(id);
    console.log(JSON.stringify({ Done: id }, null, 2));
    break;
  case 'remove':
    if (!id) {
      console.error('Missing ID for remove command');
      process.exit(1);
    }
    taskStore.remove(id);
    console.log(JSON.stringify({ Removed: id }, null, 2));
    break;
  default:
    showHelp();
    process.exit(1);
}

declare const require: any;
declare const process: any;
const fs = require('node:fs');
const crypto = require('node:crypto');

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  private readTasks(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data);
    } catch (error) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  private writeTasks(tasks: Task[]): void {
    fs.writeFileSync(this.filePath, JSON.stringify(tasks, null, 2), 'utf-8');
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.readTasks();
    tasks.push(task);
    this.writeTasks(tasks);
    return task;
  }

  list(): Task[] {
    return this.readTasks();
  }

  done(id: string): void {
    let tasks = this.readTasks();
    tasks = tasks.map(task => {
      if (task.id === id) {
        task.done = true;
        task.doneAt = new Date().toISOString();
      }
      return task;
    });
    this.writeTasks(tasks);
  }

  remove(id: string): void {
    let tasks = this.readTasks();
    tasks = tasks.filter(task => task.id !== id);
    this.writeTasks(tasks);
  }
}

export default TaskStore;
