import { readFileSync, writeFileSync } from 'fs';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  list(): Task[] {
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        writeFileSync(this.filePath, '{}', 'utf-8');
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: crypto.randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString(),
    };

    const tasks = this.list();
    tasks.push(task);
    writeFileSync(this.filePath, JSON.stringify({ tasks }), 'utf-8');
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const taskIndex = tasks.findIndex(t => t.id === id);

    if (taskIndex !== -1) {
      tasks[taskIndex] = { ...tasks[taskIndex], done: true, doneAt: new Date().toISOString() };
      writeFileSync(this.filePath, JSON.stringify({ tasks }), 'utf-8');
      return tasks[taskIndex];
    }

    throw new Error('Task not found');
  }
}

export default TaskStore;

import { program } from 'commander';
import TaskStore from './store';

const taskStore = new TaskStore('tasks.json');

program
  .name('task-cli')
  .description('A simple CLI for managing tasks.')
  .version('1.0.0');

program
  .command('list')
  .description('List all tasks.')
  .action(() => {
    const tasks = taskStore.list();
    console.log(tasks);
  });

program
  .command('add <title>')
  .description('Add a new task.')
  .action((title) => {
    const task = taskStore.add(title);
    console.log(`Task added: ${task.title}`);
  });

program
  .command('done <id>')
  .description('Mark a task as done.')
  .action((id) => {
    try {
      const task = taskStore.done(id);
      console.log(`Task marked as done: ${task.title}`);
    } catch (error) {
      console.error(error.message);
    }
  });

program.parse();
