      "path": "src/store.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = filePath;
  }

  private readTasks(): Task[] {
    try {
      const data = fs.readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data);
    } catch (error) {
      return [];
    }
  }

  private writeTasks(tasks: Task[]): void {
    fs.writeFileSync(this.filePath, JSON.stringify(tasks, null, 2));
  }

  addTask(title: string): Task {
    const tasks = this.readTasks();
    const newTask: Task = {
      id: Date.now().toString(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    tasks.push(newTask);
    this.writeTasks(tasks);
    return newTask;
  }

  listTasks(): Task[] {
    return this.readTasks();
  }

  markTaskAsDone(id: string): void {
    const tasks = this.readTasks();
    const taskIndex = tasks.findIndex(task => task.id === id);
    if (taskIndex !== -1) {
      tasks[taskIndex].done = true;
      tasks[taskIndex].doneAt = new Date().toISOString();
      this.writeTasks(tasks);
    }
  }

  removeTask(id: string): void {
    const tasks = this.readTasks();
    const updatedTasks = tasks.filter(task => task.id !== id);
    this.writeTasks(updatedTasks);
  }
}

export default TaskStore;"
    },
    {
      "path": "src/cli.ts",
      "content": "declare const require: any;
declare const process: any;
const fs = require('node:fs');
import TaskStore from './store';

const args = process.argv.slice(2);
const command = args[0];
const dataPath = args[args.length - 1];
const taskTitle = args[1] || '';
const taskId = args[1] || '';

if (command === '--help') {
  console.log('Usage:');
  console.log('- add <title> --data <path>');
  console.log('- list --data <path>');
  console.log('- done <id> --data <path>');
  console.log('- remove <id> --data <path>');
} else if (command === 'add' && taskTitle && dataPath) {
  const taskStore = new TaskStore(dataPath);
  const task = taskStore.addTask(taskTitle);
  console.log(`Added task: ${task.title}`);
} else if (command === 'list' && dataPath) {
  const taskStore = new TaskStore(dataPath);
  const tasks = taskStore.listTasks();
  console.log('Tasks:', JSON.stringify(tasks, null, 2));
} else if (command === 'done' && taskId && dataPath) {
  const taskStore = new TaskStore(dataPath);
  taskStore.markTaskAsDone(taskId);
  console.log(`Marked task ${taskId} as done`);
} else if (command === 'remove' && taskId && dataPath) {
  const taskStore = new TaskStore(dataPath);
  taskStore.removeTask(taskId);
  console.log(`Removed task ${taskId}`);
} else {
  console.error('Invalid command or arguments. Use --help for usage instructions.');
}"
