declare const process: any;

const fs = require('node:fs');
const crypto = require('node:crypto');

import { TaskStore } from './store';

function help() {
  console.log(`Usage:
  list --data <path>
  add <title> --data <path>
  done <id> --data <path>
  remove <id> --data <path>`);
  process.exit(0);
}

const argv = process.argv.slice(2);
const cmd = argv[0];
const dataPathIndex = argv.indexOf('--data');
const dataPath = dataPathIndex !== -1 ? argv[dataPathIndex + 1] : null;

if (cmd === '--help' || !dataPath) {
  help();
}

const store = new TaskStore(dataPath);

try {
  switch (cmd) {
    case 'list':
      console.log(JSON.stringify({ ok: true, tasks: store.list() }));
      break;
    case 'add':
      const title = argv[1];
      if (!title) throw new Error('Title is required');
      console.log(JSON.stringify({ ok: true, task: store.add(title) }));
      break;
    case 'done':
      const doneId = argv[1];
      if (!doneId) throw new Error('ID is required');
      console.log(JSON.stringify({ ok: true, task: store.done(doneId) }));
      break;
    case 'remove':
      const removeId = argv[1];
      if (!removeId) throw new Error('ID is required');
      console.log(JSON.stringify({ ok: true, task: store.remove(removeId) }));
      break;
    default:
      help();
  }
} catch (error: any) {
  console.error(JSON.stringify({ ok: false, error: error.message }));
  process.exit(1);
}
