      "path": "src/cli.ts",
      "content": "import * as fs from 'fs';
import * as path from 'path';
import TaskStore from './store';

const argv = process.argv.slice(2);
const command = argv[0];
const filePath = path.join(__dirname, 'tasks.json');
const taskStore = new TaskStore(filePath);

switch (command) {
  case 'add':
    const title = argv[1];
    if (!title) {
      console.error('Title is required for adding a task.');
      process.exit(1);
    }
    const newTask = taskStore.addTask(title);
    console.log(`Added task: ${newTask.title}`);
    break;
  case 'list':
    const tasks = taskStore.listTasks();
    tasks.forEach(task => {
      console.log(`${task.id}: ${task.title} [${task.done ? 'Done' : 'Pending'}]`);
    });
    break;
  case 'done':
    const taskId = argv[1];
    if (!taskId) {
      console.error('Task ID is required to mark a task as done.');
      process.exit(1);
    }
    taskStore.markTaskAsDone(taskId);
    console.log(`Marked task ${taskId} as done.`);
    break;
  case 'remove':
    const removeTaskId = argv[1];
    if (!removeTaskId) {
      console.error('Task ID is required to remove a task.');
      process.exit(1);
    }
    taskStore.removeTask(removeTaskId);
    console.log(`Removed task ${removeTaskId}.`);
    break;
  case 'help':
    console.log(
      `Usage: node dist/cli.js <command> [options]

Commands:
  add <title>     Add a new task with the given title.
  list            List all tasks.
  done <id>       Mark the task with the given ID as done.
  remove <id>     Remove the task with the given ID.
  help            Show this help message.`
    );
    break;
  default:
    console.error(`Unknown command: ${command}. Use 'help' to see available commands.`);
    process.exit(1);
}"
    }
