{
  "name": "todo-app",
  "version": "1.0.0",
  "description": "A simple todo app with CRUD operations",
  "main": "index.js",
  "scripts": {
    "test": "node tests/oracle.test.js"
  },
  "dependencies": {},
  "type": "commonjs"
}

'use strict';

const http = require('http');
const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

function createServer({ dataPath }) {
  const server = http.createServer((req, res) => {
    let body = '';
    req.on('data', chunk => {
      body += chunk.toString();
    });

    req.on('end', async () => {
      try {
        if (req.method === 'GET' && req.url === '/health') {
          res.writeHead(200, { 'Content-Type': 'application/json' });
          res.end(JSON.stringify({ ok: true }));
        } else if (req.method === 'GET' && req.url === '/openapi.json') {
          const openapiSpec = JSON.parse(fs.readFileSync(path.join(__dirname, '../openapi.json'), 'utf8'));
          res.writeHead(200, { 'Content-Type': 'application/json' });
          res.end(JSON.stringify(openapiSpec));
        } else if (req.method === 'GET' && req.url.startsWith('/todos')) {
          const url = new URL(req.url, `http://${req.headers.host}`);
          let todos = [];
          if (fs.existsSync(dataPath)) {
            todos = JSON.parse(fs.readFileSync(dataPath, 'utf8')).todos || [];
          }

          if (url.pathname === '/todos') {
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: true, todos }));
          } else if (url.pathname.startsWith('/todos/')) {
            const id = url.pathname.split('/')[2];
            const todo = todos.find(t => t.id === id);
            if (todo) {
              res.writeHead(200, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: true, todo }));
            } else {
              res.writeHead(404, { 'Content-Type': 'application/json' });
              res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
            }
          }
        } else if (req.method === 'POST' && req.url === '/todos') {
          const todo = JSON.parse(body);
          if (!todo.title) {
            res.writeHead(400, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false, error: 'Title is required' }));
            return;
          }

          const newTodo = {
            id: crypto.randomUUID(),
            title: todo.title,
            done: false,
            createdAt: new Date().toISOString()
          };

          let todos = [];
          if (fs.existsSync(dataPath)) {
            todos = JSON.parse(fs.readFileSync(dataPath, 'utf8')).todos || [];
          }

          todos.push(newTodo);
          fs.writeFileSync(dataPath, JSON.stringify({ todos }, null, 2));

          res.writeHead(201, { 'Content-Type': 'application/json' });
          res.end(JSON.stringify({ ok: true, todo: newTodo }));
        } else if (req.method === 'PATCH' && req.url.startsWith('/todos/')) {
          const url = new URL(req.url, `http://${req.headers.host}`);
          const id = url.pathname.split('/')[2];
          let todos = [];
          if (fs.existsSync(dataPath)) {
            todos = JSON.parse(fs.readFileSync(dataPath, 'utf8')).todos || [];
          }

          const todoIndex = todos.findIndex(t => t.id === id);
          if (todoIndex !== -1) {
            const update = JSON.parse(body);
            todos[todoIndex] = { ...todos[todoIndex], ...update };
            fs.writeFileSync(dataPath, JSON.stringify({ todos }, null, 2));

            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: true, todo: todos[todoIndex] }));
          } else {
            res.writeHead(404, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
          }
        } else if (req.method === 'DELETE' && req.url.startsWith('/todos/')) {
          const url = new URL(req.url, `http://${req.headers.host}`);
          const id = url.pathname.split('/')[2];
          let todos = [];
          if (fs.existsSync(dataPath)) {
            todos = JSON.parse(fs.readFileSync(dataPath, 'utf8')).todos || [];
          }

          const todoIndex = todos.findIndex(t => t.id === id);
          if (todoIndex !== -1) {
            todos.splice(todoIndex, 1);
            fs.writeFileSync(dataPath, JSON.stringify({ todos }, null, 2));

            res.writeHead(204);
            res.end();
          } else {
            res.writeHead(404, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ ok: false, error: 'Todo not found' }));
          }
        } else {
          res.writeHead(404, { 'Content-Type': 'text/plain' });
          res.end('Not Found');
        }
      } catch (err) {
        console.error(err);
        res.writeHead(500, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ ok: false, error: 'Internal Server Error' }));
      }
    });
  });

  return server;
}

module.exports = createServer;

{
  "openapi": "3.0.0",
  "info": {
    "title": "Todo API",
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "http://localhost:3000"
    }
  ],
  "paths": {
    "/health": {
      "get": {
        "summary": "Check server health",
        "responses": {
          "200": {
            "description": "Server is healthy",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "ok": {
                      "type": "boolean"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/todos": {
      "get": {
        "summary": "Get all todos",
        "responses": {
          "200": {
            "description": "A list of todos",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "ok": {
                      "type": "boolean"
                    },
                    "todos": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/Todo"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "summary": "Create a new todo",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Todo"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Todo created",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TodoResponse"
                }
              }
            }
          },
          "400": {
            "description": "Bad request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/todos/{id}": {
      "get": {
        "summary": "Get a todo by ID",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A todo",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TodoResponse"
                }
              }
            }
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "patch": {
        "summary": "Update a todo by ID",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/TodoUpdate"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Todo updated",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TodoResponse"
                }
              }
            }
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "delete": {
        "summary": "Delete a todo by ID",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Todo deleted"
          },
          "404": {
            "description": "Todo not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Todo": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string"
          }
        },
        "required": [
          "title"
        ]
      },
      "TodoUpdate": {
        "type": "object",
        "properties": {
          "done": {
            "type": "boolean"
          }
        }
      },
      "TodoResponse": {
        "type": "object",
        "properties": {
          "ok": {
            "type": "boolean"
          },
          "todo": {
            "$ref": "#/components/schemas/Todo"
          }
        }
      },
      "Error": {
        "type": "object",
        "properties": {
          "ok": {
            "type": "boolean"
          },
          "error": {
            "type": "string"
          }
        }
      }
    }
  }
}
