      "content": "const fs = require('node:fs');
const path = require('node:path');
const crypto = require('node:crypto');

import { TaskStore } from './store';

declare const require: any;
declare const process: any;

declare interface CommandArgs {
  cmd: string;
  dataPath: string;
  titleOrId: string;
}

function parseArgs(): CommandArgs {
  const args = process.argv.slice(2);
  let cmd = '';
  let dataPath = '';
  let titleOrId = '';

  for (let i = 0; i < args.length; i++) {
    if (args[i] === '--data') {
      dataPath = args[++i];
    } else if (!cmd) {
      cmd = args[i];
    } else {
      titleOrId = args[i];
    }
  }

  return { cmd, dataPath, titleOrId };
}

function help() {
  console.log(`
Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>
`);
}

async function main() {
  const { cmd, dataPath, titleOrId } = parseArgs();

  if (cmd === '--help' || !dataPath) {
    help();
    return;
  }

  const taskStore = new TaskStore(dataPath);

  try {
    switch (cmd) {
      case 'add':
        if (!titleOrId) throw new Error('Missing title');
        console.log(JSON.stringify({ ok: true, task: taskStore.add(titleOrId) }));
        break;
      case 'list':
        console.log(JSON.stringify({ ok: true, tasks: taskStore.list() }));
        break;
      case 'done':
        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: taskStore.done(titleOrId) }));
        break;
      case 'remove':
        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: taskStore.remove(titleOrId) }));
        break;
      default:
        help();
    }
  } catch (error: any) {
    console.error(JSON.stringify({ ok: false, error: error.message }));
  }
}

main();"
