      "path": "src/store.ts",
      "content": "import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';
import { randomUUID } from 'crypto';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = join(__dirname, '..', filePath);
  }

  list(): Task[] {
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        writeFileSync(this.filePath, JSON.stringify({ tasks: [] }, null, 2));
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): void {
    let tasks = this.list();
    tasks = tasks.filter(t => t.id !== id);
    writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
  }
}

export default TaskStore;"
    },
    {
      "path": "src/cli.ts",
      "content": "import { argv } from 'process';
import TaskStore from './store';

const args = argv.slice(2);
const command = args[0];
const dataPath = args.find(arg => arg.startsWith('--data='))?.split('=')[1];

if (command === '--help' || !dataPath) {
  console.log('Usage: node dist/cli.js <command> --data=<path>');
  process.exit(0);
}

const taskStore = new TaskStore(dataPath);

switch (command) {
  case 'add': {
    const title = args.find(arg => !arg.startsWith('--'));
    if (!title) {
      console.log('Title is required for add command.');
      process.exit(1);
    }
    const task = taskStore.add(title);
    console.log(JSON.stringify({ ok: true, task }));
    break;
  }
  case 'list': {
    const tasks = taskStore.list();
    console.log(JSON.stringify({ ok: true, tasks }));
    break;
  }
  case 'done': {
    const id = args.find(arg => !arg.startsWith('--'));
    if (!id) {
      console.log('ID is required for done command.');
      process.exit(1);
    }
    const task = taskStore.done(id);
    console.log(JSON.stringify({ ok: true, task }));
    break;
  }
  case 'remove': {
    const id = args.find(arg => !arg.startsWith('--'));
    if (!id) {
      console.log('ID is required for remove command.');
      process.exit(1);
    }
    taskStore.remove(id);
    console.log(JSON.stringify({ ok: true }));
    break;
  }
  default:
    console.log(`Unknown command: ${command}`);
    process.exit(1);
}"
    }
  ],
  "notes": "Updated the TaskStore and CLI implementation to ensure proper JSON output and error handling."
}
