    {
      "path": "src/store.ts",
      "content": "import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';
import { randomUUID } from 'crypto';

interface Task {
  id: string;
  title: string;
  done: boolean;
  createdAt: string;
  doneAt?: string;
}

class TaskStore {
  private filePath: string;

  constructor(filePath: string) {
    this.filePath = join(__dirname, '..', filePath);
  }

  list(): Task[] {
    try {
      const data = readFileSync(this.filePath, 'utf-8');
      return JSON.parse(data).tasks || [];
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        writeFileSync(this.filePath, JSON.stringify({ tasks: [] }, null, 2));
        return [];
      }
      throw error;
    }
  }

  add(title: string): Task {
    const task: Task = {
      id: randomUUID(),
      title,
      done: false,
      createdAt: new Date().toISOString()
    };
    const tasks = this.list();
    tasks.push(task);
    writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    return task;
  }

  done(id: string): Task {
    let tasks = this.list();
    const task = tasks.find(t => t.id === id);
    if (task) {
      task.done = true;
      task.doneAt = new Date().toISOString();
      writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
    }
    return task || { id, title: '', done: false, createdAt: '' };
  }

  remove(id: string): void {
    let tasks = this.list();
    tasks = tasks.filter(t => t.id !== id);
    writeFileSync(this.filePath, JSON.stringify({ tasks }, null, 2));
  }
}

export default TaskStore;"
    },
    {
      "path": "src/cli.ts",
      "content": "import { program } from 'commander';
import TaskStore from './store';

program
  .option('--data <path>', 'Path to the task data file')
  .helpOption('-h, --help', 'Display help information');

program
  .command('add <title>')
  .action((title: string, options) => {
    if (!options.data) {
      console.error('--data option is required');
      process.exit(1);
    }
    const taskStore = new TaskStore(options.data);
    const task = taskStore.add(title);
    console.log(`Task added: ${task.title}`);
  });

program
  .command('list')
  .action((options) => {
    if (!options.data) {
      console.error('--data option is required');
      process.exit(1);
    }
    const taskStore = new TaskStore(options.data);
    const tasks = taskStore.list();
    console.log('Tasks:', tasks);
  });

program
  .command('done <id>')
  .action((id: string, options) => {
    if (!options.data) {
      console.error('--data option is required');
      process.exit(1);
    }
    const taskStore = new TaskStore(options.data);
    const task = taskStore.done(id);
    console.log(`Task marked as done: ${task.title}`);
  });

program
  .command('remove <id>')
  .action((id: string, options) => {
    if (!options.data) {
      console.error('--data option is required');
      process.exit(1);
    }
    const taskStore = new TaskStore(options.data);
    taskStore.remove(id);
    console.log(`Task removed with id: ${id}`);
  });

program.parse(process.argv);"
    }
  ],
