import { TaskStore } from './store';

function parseArgs() {
  const args = process.argv.slice(2);
  const cmd = args[0];
  let dataPath = '';
  let titleOrId = '';

  for (let i = 1; i < args.length; i++) {
    if (args[i] === '--data') {
      dataPath = args[i + 1];
      i++;
    } else {
      titleOrId = args[i];
    }
  }

  return { cmd, dataPath, titleOrId };
}

function help() {
  console.log(`
Usage:
  add <title> --data <path>
  list --data <path>
  done <id> --data <path>
  remove <id> --data <path>
`);
}

async function main() {
  const { cmd, dataPath, titleOrId } = parseArgs();

  if (cmd === '--help') {
    help();
    return;
  }

  if (!dataPath) {
    console.error('Missing --data path');
    return;
  }

  const taskStore = new TaskStore(dataPath);

  try {
    switch (cmd) {
      case 'add':
        if (!titleOrId) throw new Error('Missing title');
        console.log(JSON.stringify({ ok: true, task: taskStore.add(titleOrId) }));
        break;
      case 'list':
        console.log(JSON.stringify({ ok: true, tasks: taskStore.list() }));
        break;
