        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: taskStore.done(titleOrId) }));
        break;
      case 'remove':
        if (!titleOrId) throw new Error('Missing id');
        console.log(JSON.stringify({ ok: true, task: taskStore.remove(titleOrId) }));
        break;
      default:
        help();
    }
  } catch (error: any) {
